{/* ðŸ§© Fila con Fecha y Hora */}
<div className="flex gap-3 mb-3">
  {/* Fecha */}
  <div className="flex flex-col flex-1">
    <label className="block text-sm mb-1">Fecha</label>
    <input
      type="date"
      value={form.datetime ? form.datetime.split("T")[0] : ""}
      onChange={(e) =>
        setForm((f) => ({
          ...f,
          datetime: e.target.value
            ? `${e.target.value}T${(f.datetime || "00:00").split("T")[1] || "00:00"}`
            : "",
        }))
      }
      className="border w-full rounded px-2 py-1"
    />
  </div>

  {/* Hora (acepta ":" "." o "," y normaliza a "HH:MM") */}
<div className="flex flex-col w-32">
  <label className="block text-sm mb-1">Hora</label>
  <input
    type="text"
    placeholder="Ej: 14:30"
    value={
      form.datetime && form.datetime.includes("T")
        ? form.datetime.split("T")[1].slice(0, 5)
        : ""
    }
    onChange={(e) => {
      const raw = e.target.value;
      // Reemplaza . y , por : y elimina cualquier otro carÃ¡cter
      const cleaned = raw.replace(',', ':').replace('.', ':').replace(/[^0-9:]/g, '');
      let truncated = cleaned.slice(0, 5);

      // Si escriben 1130 â†’ convierte a 11:30 automÃ¡ticamente
      const onlyDigits = truncated.replace(/:/g, '');
      if (onlyDigits.length === 4 && !truncated.includes(':')) {
        truncated = onlyDigits.slice(0, 2) + ':' + onlyDigits.slice(2);
      }

      setForm((f) => {
        const datePart = f.datetime ? f.datetime.split('T')[0] : '';
        return { ...f, datetime: datePart ? `${datePart}T${truncated}` : `2025-01-01T${truncated}` };
      });
    }}
    onBlur={(e) => {
      const val = e.target.value
        .replace(',', ':')
        .replace('.', ':')
        .replace(/[^0-9:]/g, '')
        .slice(0, 5);

      if (!val) {
        setForm((f) => ({
          ...f,
          datetime: f.datetime ? f.datetime.split('T')[0] + 'T' : '',
        }));
        return;
      }
      let [h, m] = val.split(':');
      if (!m) m = '00';
      if (h.length === 1) h = '0' + h;
      if (m.length === 1) m = '0' + m;
      let hi = parseInt(h || '0', 10);
      let mi = parseInt(m || '0', 10);
      if (isNaN(hi)) hi = 0;
      if (isNaN(mi)) mi = 0;
      if (hi < 0) hi = 0;
      if (hi > 23) hi = 23;
      if (mi < 0) mi = 0;
      if (mi > 59) mi = 59;
      const normalized = `${String(hi).padStart(2, '0')}:${String(mi).padStart(2, '0')}`;
      setForm((f) => {
        const datePart = f.datetime ? f.datetime.split('T')[0] : '';
        return { ...f, datetime: datePart ? `${datePart}T${normalized}` : `2025-01-01T${normalized}` };
      });
    }}
    className="border w-full rounded px-2 py-1"
    onFocus={(e) => e.target.select()}
    onWheel={(e) => e.target.blur()}
  />
</div>
</div>
