<div className="w-full sm:w-40">
  <label className="text-sm font-medium text-gray-700 mb-1 block">Hora</label>
  <input
    type="text"
    placeholder="Ej: 14:30"
    value={
      form.datetime && form.datetime.includes("T")
        ? form.datetime.split("T")[1].slice(0, 5)
        : ""
    }
    onChange={(e) => {
      let raw = e.target.value;
      raw = raw.replace(",", ":").replace(".", ":").replace(/[^0-9:]/g, "").slice(0, 5);

      // ðŸ‘‡ Si el usuario escribe 9:30, lo convertimos a 09:30 automÃ¡ticamente
      if (/^\d:\d{2}$/.test(raw)) {
        raw = "0" + raw;
      }

      // Si el usuario pone 930 o 0930 â†’ formateamos a 09:30
      const onlyDigits = raw.replace(/:/g, "");
      if (onlyDigits.length === 3) {
        raw = "0" + onlyDigits[0] + ":" + onlyDigits.slice(1);
      } else if (onlyDigits.length === 4 && !raw.includes(":")) {
        raw = onlyDigits.slice(0, 2) + ":" + onlyDigits.slice(2);
      }

      const datePart = form.datetime ? form.datetime.split("T")[0] : "";
      setForm((f) => ({
        ...f,
        datetime: datePart ? `${datePart}T${raw}` : `2025-01-01T${raw}`,
      }));
    }}
    className="w-full rounded-lg border border-gray-300 px-3 py-2 text-sm focus:border-blue-500 focus:ring focus:ring-blue-200 outline-none transition"
