// Cargar inicial y suscribirse a cambios realtime
useEffect(() => {
  let mounted = true;

  const loadInitial = async () => {
    try {
      const [c, s] = await Promise.all([
        fetchClientsFromSupabase(),
        fetchServicesFromSupabase(),
      ]);
      if (!mounted) return;
      setClients(c);
      setServices(s);
    } catch (err) {
      console.error("Error cargando datos iniciales:", err);
    }
  };
  loadInitial();

  // ðŸŸ¢ Servicios
  const servicesChannel = supabase
    .channel("realtime-services")
    .on(
      "postgres_changes",
      { event: "*", schema: "public", table: "services" },
      (payload) => {
        const { eventType, new: nuevo, old } = payload;

        setServices((prev) => {
          switch (eventType) {
            case "INSERT":
 toast.success(`ðŸ“¦ Nuevo servicio agendado: ${nuevo.clientName}`, {
  duration: 8000 // â±ï¸ dura 8 segundos (podÃ©s poner 10000 = 10s, etc.)
});
              return [...prev, nuevo].sort(
                (a, b) => new Date(a.datetime) - new Date(b.datetime)
              );
            case "UPDATE":
              toast(`ðŸ§° Servicio actualizado: ${nuevo.clientName}`, {
                icon: "ðŸ”„", 
                duration: 8000, // tiempo en milisegundos (8000 = 8 segundos)
              });
              return prev
                .map((s) => (s.id === nuevo.id ? nuevo : s))
                .sort((a, b) => new Date(a.datetime) - new Date(b.datetime));
            case "DELETE":
              toast.error(`âŒ Servicio eliminado`,{ duration: 8000, // â±ï¸ dura 8 segundos
});
              return prev.filter((s) => s.id !== old.id);
            default:
              return prev;
          }
        });
      }
    )
    .subscribe();

  // ðŸŸ¢ Clientes
  const clientsChannel = supabase
    .channel("realtime-clients")
    .on(
      "postgres_changes",
      { event: "*", schema: "public", table: "clients" },
      (payload) => {
        const { eventType, new: nuevo, old } = payload;

        setClients((prev) => {
          switch (eventType) {
            case "INSERT":
              toast.success(`ðŸ‘¤ Nuevo cliente agregado: ${nuevo.name}`,{ duration: 8000, // â±ï¸ dura 8 segundos
});
              return [...prev, nuevo].sort((a, b) =>
                (a.name || "").localeCompare(b.name || "")
              );
            case "UPDATE":
              toast(`âœï¸ Cliente actualizado: ${nuevo.name}`, { icon: "ðŸ§¾" },
                { duration: 8000, // â±ï¸ dura 8 segundos
});
              return prev
                .map((c) => (c.id === nuevo.id ? nuevo : c))
