                );
            case "DELETE":
              toast.error(`âŒ Cliente eliminado`,{ duration: 8000, // â±ï¸ dura 8 segundos
});
              return prev.filter((c) => c.id !== old.id);
            default:
              return prev;
          }
        });
      }
    )
    .subscribe();

  return () => {
    mounted = false;
    supabase.removeChannel(servicesChannel);
    supabase.removeChannel(clientsChannel);
  };
}, []);

