import React, { useState, useMemo, useEffect, useRef } from 'react';

// Agenda Técnica Office2000 - Prototipo v1.2 (mejorado)
// Cambios principales:
// - Buscador de clientes funcional
// - Autocompletado de cliente en formulario de servicio (sugiere clientes existentes y completa dirección/tel)
// - Botón "Finalizado" en la agenda diaria para marcar servicio como finalizado

export default function AgendaTecnicaOffice2000() {
  const TECHS = [
    { id: 'luciano', name: 'Luciano Comunale' },
    { id: 'eduardo', name: 'Eduardo Antúnez' },
  ];

  const LS_CLIENTS_KEY = 'agenda_clients_v1';
  const LS_SERVICES_KEY = 'agenda_services_v1';

  const loadClients = () => {
    try {
      const raw = localStorage.getItem(LS_CLIENTS_KEY);
      if (!raw) return [
        { id: 'c1', name: 'Ana Pérez', address: 'Av. 18 de Julio 1234', phone: '+598 99 123 456' },
        { id: 'c2', name: 'Taller XYZ', address: 'Calle Falsa 221', phone: '+598 98 765 432' },
      ];
      return JSON.parse(raw);
    } catch (e) {
      console.error('Error leyendo clients desde localStorage', e);
      return [];
    }
  };

  const loadServices = () => {
    try {
      const raw = localStorage.getItem(LS_SERVICES_KEY);
      if (!raw) {
        const base = [
          {
            id: 's1',
            clientId: 'c1',
            clientName: 'Ana Pérez',
            address: 'Av. 18 de Julio 1234',
            phone: '+598 99 123 456',
            type: 'Impresora chorro de tinta',
            brand: 'Brother',
            model: 'DCP-L3550',
            description: 'No imprime magenta',
            datetime: new Date(new Date().setHours(10, 0, 0, 0)).toISOString(),
            status: 'pendiente',
            tech: 'luciano',
          },
          {
            id: 's2',
            clientId: 'c2',
            clientName: 'Taller XYZ',
            address: 'Calle Falsa 221',
            phone: '+598 98 765 432',
            type: 'Notebook',
            brand: 'Lenovo',
            model: 'ThinkPad X1',
            description: 'Pantalla negra al iniciar',
            datetime: new Date(new Date().setHours(15, 30, 0, 0)).toISOString(),
            status: 'en curso',
            tech: 'eduardo',
          },
        ];
        localStorage.setItem(LS_SERVICES_KEY, JSON.stringify(base));
        return base;
      }
      return JSON.parse(raw);
    } catch (e) {
      console.error('Error leyendo services desde localStorage', e);
      return [];
    }
  };

  const [clients, setClients] = useState(() => loadClients());
  const [services, setServices] = useState(() => loadServices());

  useEffect(() => { try { localStorage.setItem(LS_CLIENTS_KEY, JSON.stringify(clients)); } catch (e) {} }, [clients]);
  useEffect(() => { try { localStorage.setItem(LS_SERVICES_KEY, JSON.stringify(services)); } catch (e) {} }, [services]);

  const [selectedDate, setSelectedDate] = useState(() => {
    const d = new Date();
    d.setHours(0, 0, 0, 0);
    return d;
  });

  const emptyForm = {
    id: null,
    clientId: '',
    clientName: '',
    address: '',
    phone: '',
    type: '',
    brand: '',
    model: '',
    description: '',
    datetime: '',
    status: 'pendiente',
    tech: TECHS[0].id,
  };
  const [form, setForm] = useState(emptyForm);
  const [showForm, setShowForm] = useState(false);

  const [showClientModal, setShowClientModal] = useState(false);
  const [newClient, setNewClient] = useState({ name: '', address: '', phone: '' });
  const [view, setView] = useState('agenda');

  // ---- NUEVAS PIEZAS: buscador de clientes en vista clientes
  const [clientSearchTerm, setClientSearchTerm] = useState('');
  const filteredClients = useMemo(() => {
    const q = clientSearchTerm.trim().toLowerCase();
    if (!q) return clients;
    return clients.filter(c =>
      (c.name || '').toLowerCase().includes(q) ||
      (c.address || '').toLowerCase().includes(q) ||
      (c.phone || '').toLowerCase().includes(q)
    );
  }, [clients, clientSearchTerm]);

  // Autocomplete para campo cliente en form servicio
  const [clientSuggestions, setClientSuggestions] = useState([]);
  const suggestionsRef = useRef(null);

  useEffect(() => {
    // click fuera de sugerencias cierra la lista
    const onDoc = (e) => {
      if (suggestionsRef.current && !suggestionsRef.current.contains(e.target)) {
        setClientSuggestions([]);
      }
    };
    document.addEventListener('click', onDoc);
    return () => document.removeEventListener('click', onDoc);
  }, []);

  // Fecha / utils
  const sameDay = (d1, d2) => d1.getFullYear() === d2.getFullYear() && d1.getMonth() === d2.getMonth() && d1.getDate() === d2.getDate();

  const servicesOfDay = useMemo(() => {
    return services.filter((s) => {
      const sd = new Date(s.datetime);
      return sameDay(sd, selectedDate);
    }).sort((a, b) => new Date(a.datetime) - new Date(b.datetime));
  }, [services, selectedDate]);

  // calendario
  const monthMatrix = useMemo(() => {
    const start = new Date(selectedDate.getFullYear(), selectedDate.getMonth(), 1);
    const end = new Date(selectedDate.getFullYear(), selectedDate.getMonth() + 1, 0);
    const weeks = [];
    let week = [];
    // shift so Sunday first to match original (kept)
    const startDay = start.getDay(); // 0..6
    for (let i = 0; i < startDay; i++) week.push(null);
    for (let day = 1; day <= end.getDate(); day++) {
      week.push(new Date(selectedDate.getFullYear(), selectedDate.getMonth(), day));
      if (week.length === 7) { weeks.push(week); week = []; }
    }
    while (week.length > 0 && week.length < 7) week.push(null);
    if (week.length) weeks.push(week);
    return weeks;
  }, [selectedDate]);

  const countByDay = (date) => {
    if (!date) return 0;
    return services.filter((s) => sameDay(new Date(s.datetime), date)).length;
  };

  // ---- Handlers y lógica ----
  const openNewService = (prefillDate) => {
    const iso = prefillDate ? new Date(prefillDate).toISOString().slice(0,16) : '';
    setForm({...emptyForm, datetime: iso});
    setClientSuggestions([]);
    setShowForm(true);
  };

  const openEditService = (svc) => {
    setForm({ ...svc, datetime: new Date(svc.datetime).toISOString().slice(0,16) });
    setClientSuggestions([]);
    setShowForm(true);
  };

  const saveService = () => {
    if (!form.clientName || !form.datetime) {
      alert('Complete cliente y fecha/hora');
      return;
    }
    // Si el nombre escrito coincide con un cliente existente, asociarlo
    const match = clients.find(c => c.name.toLowerCase() === (form.clientName || '').trim().toLowerCase());
    const clientId = match ? match.id : form.clientId || '';

    // convertir datetime a ISO
    const isoDatetime = new Date(form.datetime).toISOString();

    if (form.id) {
      // actualizar
      setServices(prev => prev.map(p => p.id === form.id ? { ...p, ...form, clientId: clientId || p.clientId, clientName: form.clientName, datetime: isoDatetime } : p));
    } else {
      const id = 's' + (Math.random().toString(36).slice(2,8));
      const newS = { ...form, id, clientId, clientName: form.clientName, datetime: isoDatetime };
      setServices(prev => [...prev, newS]);
    }
    setShowForm(false);
    setForm(emptyForm);
    setClientSuggestions([]);
  };

  const deleteService = (id) => {
    if (!window.confirm('Eliminar servicio?')) return;
    setServices(prev => prev.filter(s => s.id !== id));
  };

  const markFinalized = (id) => {
    setServices(prev => prev.map(s => s.id === id ? { ...s, status: 'finalizado' } : s));
  };

  const handleClientSelect = (clientId) => {
    const c = clients.find(x => x.id === clientId);
    if (c) setForm(f => ({ ...f, clientId: c.id, clientName: c.name, address: c.address, phone: c.phone }));
  };

  const addClientQuick = () => {
    if (!form.clientName) return alert('Escriba nombre para crear cliente rápido');
    // si existe, no duplicar
    const existing = clients.find(c => c.name.toLowerCase() === form.clientName.trim().toLowerCase());
    if (existing) {
      setForm(f => ({ ...f, clientId: existing.id, clientName: existing.name }));
      return;
    }
    const id = 'c' + (Math.random().toString(36).slice(2,8));
    const newC = { id, name: form.clientName, address: form.address, phone: form.phone };
    setClients(prev => [...prev, newC]);
    setForm(f => ({ ...f, clientId: id }));
  };

  const openNewClientModal = () => {
    setNewClient({ name: '', address: '', phone: '' });
    setShowClientModal(true);
  };

  const saveNewClient = () => {
    if (!newClient.name) return alert('Complete el nombre del cliente');
    if (newClient.id) {
      setClients(prev => prev.map(c => c.id === newClient.id ? { ...c, name: newClient.name, address: newClient.address, phone: newClient.phone } : c));
    } else {
      const id = 'c' + (Math.random().toString(36).slice(2,8));
      const c = { id, name: newClient.name, address: newClient.address, phone: newClient.phone };
      setClients(prev => [...prev, c]);
    }
    setShowClientModal(false);
    setNewClient({ name: '', address: '', phone: '' });
  };

  // Autocomplete: cuando escriben en cliente, filtrar sugerencias
  const onClientNameChange = (val) => {
    setForm(f => ({ ...f, clientName: val, clientId: '' })); // reset clientId hasta que elijan
    if (!val || val.trim().length < 1) { setClientSuggestions([]); return; }
    const q = val.trim().toLowerCase();
    const matches = clients.filter(c => c.name.toLowerCase().includes(q) || (c.phone||'').toLowerCase().includes(q) || (c.address||'').toLowerCase().includes(q));
    setClientSuggestions(matches.slice(0,8));
  };

  // Editar cliente desde listado
  const editClient = (c) => {
    setNewClient({ id: c.id, name: c.name, address: c.address, phone: c.phone });
    setShowClientModal(true);
  };

  // UI helpers
  const statusColor = (status) => {
    switch(status) {
      case 'pendiente': return 'bg-red-200 text-red-800';
      case 'en curso': return 'bg-yellow-200 text-yellow-800';
      case 'finalizado': return 'bg-green-200 text-green-800';
      case 'cancelado': return 'bg-gray-300 text-gray-800';
      default: return 'bg-gray-100';
    }
  };

  const today = new Date(); today.setHours(0,0,0,0);

  return (
    <div className="min-h-screen bg-gray-50 p-4 md:p-8 font-sans">
      <header className="max-w-7xl mx-auto mb-6 flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-semibold">Agenda Técnica Office2000</h1>
          <p className="text-sm text-gray-600">Agenda Office2000</p>
        </div>
        <div className="flex gap-2 items-center">
          <button onClick={() => { setSelectedDate(new Date()); setView('agenda'); }} className="px-3 py-2 bg-white border rounded shadow-sm">Hoy</button>
          {view === 'agenda' && <button onClick={() => openNewService(selectedDate)} className="px-4 py-2 bg-blue-600 text-white rounded shadow">Nuevo servicio</button>}
          {view === 'agenda' ? <button onClick={() => setView('clients')} className="px-3 py-2 bg-green-600 text-white rounded shadow">Clientes</button> : <button onClick={() => setView('agenda')} className="px-3 py-2 bg-white border rounded shadow-sm">← Volver</button>}
        </div>
      </header>

      <main className="max-w-7xl mx-auto grid grid-cols-1 md:grid-cols-3 gap-6">
        {view === 'agenda' ? (
          <>
            <aside className="md:col-span-1 bg-white p-4 rounded shadow-sm">
              <div className="flex items-center justify-between mb-3">
                <div>
                  <div className="text-sm text-gray-500">{selectedDate.toLocaleString('es-UY', { month: 'long', year: 'numeric' })}</div>
                  <div className="text-lg font-medium">Calendario mensual</div>
                </div>
                <div className="flex gap-1">
                  <button onClick={() => setSelectedDate(d => new Date(d.getFullYear(), d.getMonth() -1, 1))} className="px-2 py-1 border rounded">◀</button>
                  <button onClick={() => setSelectedDate(d => new Date(d.getFullYear(), d.getMonth() +1, 1))} className="px-2 py-1 border rounded">▶</button>
                </div>
              </div>

              <div className="grid grid-cols-7 gap-1 text-xs text-center text-gray-500">
                {['Dom','Lun','Mar','Mié','Jue','Vie','Sáb'].map(d => (<div key={d} className="py-1">{d}</div>))}
              </div>

              <div className="mt-2">
                {monthMatrix.map((week, i) => (
                  <div key={i} className="grid grid-cols-7 gap-1 text-sm">
                    {week.map((day, j) => {
                      const isToday = day && sameDay(day, today);
                      const isSelected = day && sameDay(day, selectedDate);
                      const cnt = day ? countByDay(day) : 0;
                      return (
                        <button
                          key={j}
                          onClick={() => day && setSelectedDate(new Date(day))}
                          className={`p-2 rounded text-left ${isSelected ? 'bg-blue-50' : 'hover:bg-gray-50'} ${isToday ? 'ring-2 ring-blue-300' : ''}`}
                        >
                          <div className="flex justify-between items-start">
                            <div className={`font-medium ${day && isToday ? 'text-blue-700' : ''}`}>{day ? day.getDate() : ''}</div>
                            <div className="text-xs text-gray-400"></div>
                          </div>
                          <div className="mt-1">
                            {cnt > 0 ? (
                              <div className="inline-block px-1 text-xs font-medium rounded text-blue-700">
                                🔹{cnt}
                              </div>
                            ) : (<div className="text-xs text-gray-300"> </div>)}
                          </div>
                        </button>
                      );
                    })}
                  </div>
                ))}
              </div>
            </aside>

            <section className="md:col-span-2">
              <div className="bg-white p-4 rounded shadow-sm">
                <div className="flex items-center justify-between mb-4">
                  <div>
                    <div className="text-sm text-gray-500">Agenda diaria</div>
                    <div className="text-xl font-semibold">{selectedDate.toLocaleDateString('es-UY', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}</div>
                  </div>
                  <div className="flex gap-2">
                    <select value={''} onChange={() => {}} className="border rounded px-2 py-1"><option>Todos los técnicos</option></select>
                  </div>
                </div>

                <div className="grid grid-cols-12 gap-4">
                  <div className="col-span-3 border-r pr-4">
                    {Array.from({length: 12}).map((_, i) => {
                      const hour = 8 + i;
                      return (<div key={i} className="h-16 flex items-start text-sm text-gray-600">{hour}:00</div>);
                    })}
                  </div>

                  <div className="col-span-9">
                    <div className="relative">
                      {servicesOfDay.length === 0 && (<div className="p-6 text-center text-gray-500">No hay servicios para este día. Hacé clic en "Nuevo servicio" para agregar uno.</div>)}

                      <div className="space-y-3">
                        {servicesOfDay.map((s) => (
                          <div key={s.id} className={`p-3 rounded shadow-sm border ${statusColor(s.status)}`}>
                            <div className="flex justify-between items-start">
                              <div>
                                <div className="font-medium">{s.clientName} — {s.type}</div>
                                <div className="text-sm text-gray-700">{s.brand} {s.model} • {new Date(s.datetime).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}</div>
                                <div className="text-sm text-gray-600 mt-1">{s.description}</div>
                                <div className="text-xs text-gray-500 mt-2">{s.address} • {s.phone}</div>
                              </div>
                              <div className="flex flex-col items-end gap-2">
                                <div className="text-xs text-gray-600">Técnico: {TECHS.find(t => t.id === s.tech)?.name}</div>
                                <div className="flex gap-1">
                                  <button onClick={() => openEditService(s)} className="px-2 py-1 text-sm border rounded">✏️ Editar</button>
                                  <button onClick={() => deleteService(s.id)} className="px-2 py-1 text-sm border rounded">Eliminar</button>
                                  <button onClick={() => markFinalized(s.id)} className="px-2 py-1 text-sm border rounded bg-green-100">Finalizado</button>
                                </div>
                              </div>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              <div className="mt-4 bg-white p-3 rounded shadow-sm">
                <div className="flex items-center justify-between mb-2">
                  <div className="text-sm text-gray-600">Servicios programados ({servicesOfDay.length})</div>
                </div>
                <div className="space-y-2">
                  {servicesOfDay.map(s => (
                    <div key={s.id} className="flex items-center justify-between border rounded p-2">
                      <div>
                        <div className="font-medium">{s.clientName} <span className="text-xs text-gray-500">• {s.type}</span></div>
                        <div className="text-xs text-gray-500">{new Date(s.datetime).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})} • {TECHS.find(t => t.id === s.tech)?.name}</div>
                      </div>
                      <div className="flex gap-2">
                        <div className={`px-2 py-1 text-xs rounded ${statusColor(s.status)}`}>{s.status}</div>
                        <button onClick={() => openEditService(s)} className="px-2 py-1 border rounded text-sm">Ver / Editar</button>
                        <button onClick={() => markFinalized(s.id)} className="px-2 py-1 border rounded text-sm bg-green-50">Finalizado</button>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </section>
          </>
        ) : (
          <section className="md:col-span-3">
            <div className="bg-white p-4 rounded shadow-sm">
              <div className="flex items-center justify-between mb-4">
                <div>
                  <div className="text-sm text-gray-500">Clientes registrados</div>
                  <div className="text-xl font-semibold">Lista de clientes</div>
                </div>
                <div className="flex gap-2">
                  <button onClick={openNewClientModal} className="px-4 py-2 bg-green-600 text-white rounded shadow">Nuevo cliente</button>
                </div>
              </div>

              <div className="mb-3">
                <input
                  type="text"
                  placeholder="Buscar cliente por nombre, dirección o teléfono..."
                  className="w-full border rounded px-3 py-2"
                  value={clientSearchTerm}
                  onChange={(e) => setClientSearchTerm(e.target.value)}
                />
              </div>

              <div className="space-y-2">
                {filteredClients.length === 0 ? (
                  <div className="text-gray-500 p-3">No hay clientes registrados.</div>
                ) : (
                  filteredClients.map((c) => (
                    <div key={c.id} className="border rounded p-3 flex items-center justify-between">
                      <div>
                        <div className="font-medium">{c.name}</div>
                        <div className="text-sm text-gray-500">{c.address} • {c.phone}</div>
                      </div>
                      <div className="flex gap-2">
                         {/* 🔵 Nuevo botón agregado */}
      <button
        onClick={() => {
          setView('agenda');
          setForm({
            ...emptyForm,
            clientId: c.id,
            clientName: c.name,
            address: c.address,
            phone: c.phone
          });
          setShowForm(true);
        }}
        className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700"
      >
        Agendar servicio
      </button>
                        <button onClick={() => editClient(c)} className="px-3 py-1 border rounded">Editar</button>
                        <button onClick={() => { if (!window.confirm('Eliminar cliente?')) return; setClients(prev => prev.filter(x => x.id !== c.id)); }} className="px-3 py-1 border rounded">Eliminar</button>
                      </div>
                    </div>
                  ))
                )}
              </div>
            </div>
          </section>
        )}
      </main>

      {/* Form modal servicio */}
      {showForm && (
        <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center p-4 z-40">
          <div className="bg-white w-full max-w-2xl rounded shadow-lg p-4">
            <div className="flex items-center justify-between mb-3">
              <h2 className="text-lg font-semibold">{form.id ? 'Editar servicio' : 'Nuevo servicio'}</h2>
              <div className="flex gap-2">
                <button onClick={() => { setShowForm(false); setForm(emptyForm); setClientSuggestions([]); }} className="px-3 py-1 border rounded">Cerrar</button>
                <button onClick={saveService} className="px-3 py-1 bg-blue-600 text-white rounded">Guardar</button>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
              <div>
                <label className="text-sm text-gray-600">Cliente</label>
                <div style={{ position: 'relative' }} ref={suggestionsRef}>
                  <input
                    value={form.clientName}
                    onChange={(e) => onClientNameChange(e.target.value)}
                    className="w-full border rounded px-2 py-1"
                    placeholder="Nombre del cliente"
                    autoComplete="off"
                  />
                  {/* lista de sugerencias */}
                  {clientSuggestions.length > 0 && (
                    <div className="absolute left-0 right-0 bg-white border mt-1 rounded shadow z-50" style={{ maxHeight: 180, overflow: 'auto' }}>
                      {clientSuggestions.map(c => (
                        <div key={c.id} className="px-2 py-2 hover:bg-gray-100 cursor-pointer" onClick={() => {
                          setForm(f => ({ ...f, clientId: c.id, clientName: c.name, address: c.address, phone: c.phone }));
                          setClientSuggestions([]);
                        }}>
                          <div className="font-medium">{c.name}</div>
                          <div className="text-xs text-gray-500">{c.address} • {c.phone}</div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>

                <div className="flex gap-2 mt-2">
                  <button onClick={addClientQuick} className="px-3 py-1 border rounded">Crear cliente</button>
                </div>
              </div>

              <div>
                <label className="text-sm text-gray-600">Dirección</label>
                <input value={form.address} onChange={(e) => setForm(f => ({...f, address: e.target.value }))} className="w-full border rounded px-2 py-1" placeholder="Dirección" />
              </div>

              <div>
                <label className="text-sm text-gray-600">Teléfono</label>
                <input value={form.phone} onChange={(e) => setForm(f => ({...f, phone: e.target.value }))} className="w-full border rounded px-2 py-1" placeholder="Teléfono" />
              </div>

              <div>
                <label className="text-sm text-gray-600">Tipo de equipo</label>
                <input value={form.type} onChange={(e) => setForm(f => ({...f, type: e.target.value }))} className="w-full border rounded px-2 py-1" placeholder="Ej: Impresora chorro de tinta" />
              </div>

              <div>
                <label className="text-sm text-gray-600">Marca</label>
                <input value={form.brand} onChange={(e) => setForm(f => ({...f, brand: e.target.value }))} className="w-full border rounded px-2 py-1" placeholder="Marca" />
              </div>

              <div>
                <label className="text-sm text-gray-600">Modelo</label>
                <input value={form.model} onChange={(e) => setForm(f => ({...f, model: e.target.value }))} className="w-full border rounded px-2 py-1" placeholder="Modelo" />
              </div>

              <div className="md:col-span-2">
                <label className="text-sm text-gray-600">Descripción del problema</label>
                <textarea value={form.description} onChange={(e) => setForm(f => ({...f, description: e.target.value }))} className="w-full border rounded px-2 py-1" rows={3} />
              </div>

              <div>
                <label className="text-sm text-gray-600">Fecha y hora</label>
                <input type="datetime-local" 
                value={form.datetime} 
                onChange={(e) => setForm(f => ({...f, datetime: e.target.value }))} 
                onWheel={(e) => e.target.blur()} // 🔒 evita que el scroll cambie la hora
                className="w-full border rounded px-2 py-1" />
              </div>

              <div>
                <label className="text-sm text-gray-600">Estado</label>
                <select value={form.status} onChange={(e) => setForm(f => ({...f, status: e.target.value }))} className="w-full border rounded px-2 py-1">
                  <option value="pendiente">Pendiente</option>
                  <option value="en curso">En curso</option>
                  <option value="finalizado">Finalizado</option>
                  <option value="cancelado">Cancelado</option>
                </select>
              </div>

              <div>
                <label className="text-sm text-gray-600">Técnico asignado</label>
                <select value={form.tech} onChange={(e) => setForm(f => ({...f, tech: e.target.value }))} className="w-full border rounded px-2 py-1">
                  {TECHS.map(t => (<option key={t.id} value={t.id}>{t.name}</option>))}
                </select>
              </div>
            </div>

          </div>
        </div>
      )}

      {/* Modal Nuevo Cliente */}
      {showClientModal && (
        <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center p-4 z-50">
          <div className="bg-white w-full max-w-md rounded shadow-lg p-4">
            <div className="flex items-center justify-between mb-3">
              <h2 className="text-lg font-semibold">Nuevo cliente</h2>
              <div className="flex gap-2">
                <button onClick={() => setShowClientModal(false)} className="px-3 py-1 border rounded">Cerrar</button>
                <button onClick={saveNewClient} className="px-3 py-1 bg-green-600 text-white rounded">Guardar</button>
              </div>
            </div>

            <div className="grid grid-cols-1 gap-3">
              <div>
                <label className="text-sm text-gray-600">Nombre</label>
                <input value={newClient.name} onChange={(e) => setNewClient(n => ({...n, name: e.target.value }))} className="w-full border rounded px-2 py-1" placeholder="Nombre del cliente" />
              </div>
              <div>
                <label className="text-sm text-gray-600">Dirección</label>
                <input value={newClient.address} onChange={(e) => setNewClient(n => ({...n, address: e.target.value }))} className="w-full border rounded px-2 py-1" placeholder="Dirección" />
              </div>
              <div>
                <label className="text-sm text-gray-600">Teléfono</label>
                <input value={newClient.phone} onChange={(e) => setNewClient(n => ({...n, phone: e.target.value }))} className="w-full border rounded px-2 py-1" placeholder="Teléfono" />
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
