// agenda_tecnica_office_2000_prototipo.jsx
// versión mejorada con botón "Agendar servicio" en lista de clientes
import React, { useState, useMemo, useEffect, useRef } from 'react';

export default function AgendaTecnicaOffice2000() {
  const TECHS = [
    { id: 'luciano', name: 'Luciano Comunale' },
    { id: 'eduardo', name: 'Eduardo Antúnez' },
  ];

  const LS_CLIENTS_KEY = 'agenda_clients_v1';
  const LS_SERVICES_KEY = 'agenda_services_v1';

  const loadClients = () => {
    try {
      const raw = localStorage.getItem(LS_CLIENTS_KEY);
      if (!raw) return [
        { id: 'c1', name: 'Ana Pérez', address: 'Av. 18 de Julio 1234', phone: '+598 99 123 456' },
        { id: 'c2', name: 'Taller XYZ', address: 'Calle Falsa 221', phone: '+598 98 765 432' },
      ];
      return JSON.parse(raw);
    } catch (e) { return []; }
  };

  const loadServices = () => {
    try {
      const raw = localStorage.getItem(LS_SERVICES_KEY);
      if (!raw) return [];
      return JSON.parse(raw);
    } catch (e) { return []; }
  };

  const [clients, setClients] = useState(() => loadClients());
  const [services, setServices] = useState(() => loadServices());
  useEffect(() => localStorage.setItem(LS_CLIENTS_KEY, JSON.stringify(clients)), [clients]);
  useEffect(() => localStorage.setItem(LS_SERVICES_KEY, JSON.stringify(services)), [services]);

  const [selectedDate, setSelectedDate] = useState(() => {
    const d = new Date();
    d.setHours(0, 0, 0, 0);
    return d;
  });

  const [view, setView] = useState('agenda');
  const emptyForm = { id: null, clientId: '', clientName: '', address: '', phone: '', type: '', brand: '', model: '', description: '', datetime: '', status: 'pendiente', tech: TECHS[0].id };
  const [form, setForm] = useState(emptyForm);
  const [showForm, setShowForm] = useState(false);

  const [showClientModal, setShowClientModal] = useState(false);
  const [newClient, setNewClient] = useState({ name: '', address: '', phone: '' });
  const [clientSearchTerm, setClientSearchTerm] = useState('');
  const filteredClients = useMemo(() => {
    const q = clientSearchTerm.trim().toLowerCase();
    if (!q) return clients;
    return clients.filter(c =>
      (c.name || '').toLowerCase().includes(q) ||
      (c.address || '').toLowerCase().includes(q) ||
      (c.phone || '').toLowerCase().includes(q)
    );
  }, [clients, clientSearchTerm]);

  const [clientSuggestions, setClientSuggestions] = useState([]);
  const suggestionsRef = useRef(null);
  useEffect(() => {
    const onDoc = e => { if (suggestionsRef.current && !suggestionsRef.current.contains(e.target)) setClientSuggestions([]); };
    document.addEventListener('click', onDoc);
    return () => document.removeEventListener('click', onDoc);
  }, []);

  const sameDay = (d1, d2) => d1.getFullYear() === d2.getFullYear() && d1.getMonth() === d2.getMonth() && d1.getDate() === d2.getDate();
  const servicesOfDay = useMemo(() => services.filter(s => sameDay(new Date(s.datetime), selectedDate)), [services, selectedDate]);

  const openNewService = (prefillDate, prefillClient = null) => {
    const iso = prefillDate ? new Date(prefillDate).toISOString().slice(0, 16) : '';
    if (prefillClient) {
      setForm({ ...emptyForm, datetime: iso, clientId: prefillClient.id, clientName: prefillClient.name, address: prefillClient.address, phone: prefillClient.phone });
    } else {
      setForm({ ...emptyForm, datetime: iso });
    }
    setShowForm(true);
    setView('agenda');
  };

  const saveService = () => {
    if (!form.clientName || !form.datetime) return alert('Complete cliente y fecha/hora');
    const match = clients.find(c => c.name.toLowerCase() === form.clientName.toLowerCase());
    const clientId = match ? match.id : form.clientId || '';
    const iso = new Date(form.datetime).toISOString();
    if (form.id) {
      setServices(prev => prev.map(s => s.id === form.id ? { ...s, ...form, clientId, datetime: iso } : s));
    } else {
      const id = 's' + Math.random().toString(36).slice(2, 8);
      setServices(prev => [...prev, { ...form, id, clientId, datetime: iso }]);
    }
    setShowForm(false);
    setForm(emptyForm);
  };

  const deleteService = id => { if (window.confirm('Eliminar servicio?')) setServices(s => s.filter(x => x.id !== id)); };
  const markFinalized = id => setServices(s => s.map(x => x.id === id ? { ...x, status: 'finalizado' } : x));

  const onClientNameChange = val => {
    setForm(f => ({ ...f, clientName: val, clientId: '' }));
    if (!val) return setClientSuggestions([]);
    const q = val.toLowerCase();
    setClientSuggestions(clients.filter(c => c.name.toLowerCase().includes(q)).slice(0, 6));
  };

  const editClient = c => { setNewClient(c); setShowClientModal(true); };
  const openNewClientModal = () => { setNewClient({ name: '', address: '', phone: '' }); setShowClientModal(true); };
  const saveNewClient = () => {
    if (!newClient.name) return alert('Falta nombre');
    if (newClient.id) setClients(cs => cs.map(c => c.id === newClient.id ? newClient : c));
    else setClients(cs => [...cs, { ...newClient, id: 'c' + Math.random().toString(36).slice(2, 8) }]);
    setShowClientModal(false);
  };

  const statusColor = s => s === 'finalizado' ? 'bg-green-200' : s === 'pendiente' ? 'bg-red-200' : 'bg-yellow-200';

  return (
    <div className="min-h-screen bg-gray-50 p-4 font-sans">
      <header className="max-w-6xl mx-auto mb-4 flex justify-between items-center">
        <h1 className="text-2xl font-semibold">Agenda Técnica Office2000</h1>
        <div className="flex gap-2">
          {view === 'agenda'
            ? <button onClick={() => setView('clients')} className="px-3 py-2 bg-green-600 text-white rounded">Clientes</button>
            : <button onClick={() => setView('agenda')} className="px-3 py-2 bg-gray-200 rounded">← Volver</button>}
          {view === 'agenda' && <button onClick={() => openNewService(selectedDate)} className="px-3 py-2 bg-blue-600 text-white rounded">Nuevo servicio</button>}
        </div>
      </header>

      <main className="max-w-6xl mx-auto">
        {view === 'clients' ? (
          <div className="bg-white rounded p-4 shadow">
            <div className="flex justify-between items-center mb-4">
              <div>
                <h2 className="text-lg font-semibold">Clientes</h2>
                <p className="text-sm text-gray-500">Lista completa de clientes registrados</p>
              </div>
              <button onClick={openNewClientModal} className="px-3 py-2 bg-green-600 text-white rounded">Nuevo cliente</button>
            </div>

            <input type="text" placeholder="Buscar cliente..." value={clientSearchTerm} onChange={e => setClientSearchTerm(e.target.value)} className="border rounded px-3 py-2 w-full mb-3" />

            {filteredClients.length === 0 ? (
              <p className="text-gray-500">No hay clientes.</p>
            ) : (
              filteredClients.map(c => (
                <div key={c.id} className="border rounded p-3 mb-2 flex justify-between items-center">
                  <div>
                    <div className="font-medium">{c.name}</div>
                    <div className="text-sm text-gray-600">{c.address} • {c.phone}</div>
                  </div>
                  <div className="flex gap-2">
                    <button onClick={() => openNewService(new Date(), c)} className="px-3 py-1 bg-blue-600 text-white rounded">Agendar servicio</button>
                    <button onClick={() => editClient(c)} className="px-3 py-1 border rounded">Editar</button>
                    <button onClick={() => { if (window.confirm('Eliminar cliente?')) setClients(cs => cs.filter(x => x.id !== c.id)); }} className="px-3 py-1 border rounded">Eliminar</button>
                  </div>
                </div>
              ))
            )}
          </div>
        ) : (
          <div className="bg-white rounded p-4 shadow">
            <h2 className="text-lg font-semibold mb-4">Agenda diaria ({selectedDate.toLocaleDateString()})</h2>
            {servicesOfDay.length === 0 ? <p className="text-gray-500">Sin servicios este día.</p> : servicesOfDay.map(s => (
              <div key={s.id} className={`p-3 mb-2 border rounded ${statusColor(s.status)}`}>
                <div className="flex justify-between items-center">
                  <div>
                    <div className="font-medium">{s.clientName}</div>
                    <div className="text-sm">{s.brand} {s.model} — {s.description}</div>
                    <div className="text-xs text-gray-600">{new Date(s.datetime).toLocaleTimeString()} • {s.address}</div>
                  </div>
                  <div className="flex gap-2">
                    <button onClick={() => markFinalized(s.id)} className="px-2 py-1 bg-green-200 rounded text-sm">Finalizado</button>
                    <button onClick={() => deleteService(s.id)} className="px-2 py-1 border rounded text-sm">Eliminar</button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </main>

      {showForm && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center p-4">
          <div className="bg-white p-4 rounded shadow max-w-lg w-full">
            <div className="flex justify-between items-center mb-3">
              <h3 className="text-lg font-semibold">{form.id ? 'Editar servicio' : 'Nuevo servicio'}</h3>
              <button onClick={() => setShowForm(false)} className="px-2 py-1 border rounded">Cerrar</button>
            </div>

            <label className="block text-sm mb-1">Cliente</label>
            <div ref={suggestionsRef} className="relative mb-3">
              <input value={form.clientName} onChange={e => onClientNameChange(e.target.value)} className="border w-full rounded px-2 py-1" placeholder="Cliente" />
              {clientSuggestions.length > 0 && (
                <div className="absolute bg-white border rounded mt-1 w-full max-h-40 overflow-auto z-10">
                  {clientSuggestions.map(c => (
                    <div key={c.id} className="px-2 py-1 hover:bg-gray-100 cursor-pointer" onClick={() => { setForm(f => ({ ...f, clientId: c.id, clientName: c.name, address: c.address, phone: c.phone })); setClientSuggestions([]); }}>
                      {c.name} <span className="text-xs text-gray-500">{c.phone}</span>
                    </div>
                  ))}
                </div>
              )}
            </div>

            <label className="block text-sm mb-1">Dirección</label>
            <input value={form.address} onChange={e => setForm(f => ({ ...f, address: e.target.value }))} className="border w-full rounded px-2 py-1 mb-3" />

            <label className="block text-sm mb-1">Teléfono</label>
            <input value={form.phone} onChange={e => setForm(f => ({ ...f, phone: e.target.value }))} className="border w-full rounded px-2 py-1 mb-3" />

            <label className="block text-sm mb-1">Descripción</label>
            <textarea value={form.description} onChange={e => setForm(f => ({ ...f, description: e.target.value }))} className="border w-full rounded px-2 py-1 mb-3" rows="3" />

            <label className="block text-sm mb-1">Fecha y hora</label>
            <input type="datetime-local" value={form.datetime} onChange={e => setForm(f => ({ ...f, datetime: e.target.value }))} className="border w-full rounded px-2 py-1 mb-3" />

            <div className="flex justify-end gap-2">
              <button onClick={saveService} className="px-3 py-1 bg-blue-600 text-white rounded">Guardar</button>
            </div>
          </div>
        </div>
      )}

      {showClientModal && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center p-4">
          <div className="bg-white p-4 rounded shadow max-w-md w-full">
            <h3 className="text-lg font-semibold mb-3">{newClient.id ? 'Editar cliente' : 'Nuevo cliente'}</h3>
            <input placeholder="Nombre" value={newClient.name} onChange={e => setNewClient(n => ({ ...n, name: e.target.value }))} className="border w-full rounded px-2 py-1 mb-2" />
            <input placeholder="Dirección" value={newClient.address} onChange={e => setNewClient(n => ({ ...n, address: e.target.value }))} className="border w-full rounded px-2 py-1 mb-2" />
            <input placeholder="Teléfono" value={newClient.phone} onChange={e => setNewClient(n => ({ ...n, phone: e.target.value }))} className="border w-full rounded px-2 py-1 mb-3" />
            <div className="flex justify-end gap-2">
              <button onClick={() => setShowClientModal(false)} className="px-2 py-1 border rounded">Cancelar</button>
              <button onClick={saveNewClient} className="px-3 py-1 bg-green-600 text-white rounded">Guardar</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
